'use strict';

const p = msg.payload,
      log_menu = flow.get('log_menu'),
      log_s_meter = flow.get('log_s_meter'),
      log_other = flow.get('log_other');

if(p === 'log_menu') {
    flow.set('log_menu', !log_menu);
    node.status('Log menu - ' + !log_menu);
}
else if(p === 'log_s_meter') {
    flow.set('log_s_meter', !log_s_meter);
    node.status('Log S-Meter - ' + !log_s_meter);
}
else if(p === 'log_other') {
    flow.set('log_other', !log_other);
    node.status('Log Other - ' + !log_other);
}
else if(p.substring(0,2) === 'EX') {
    if(log_menu) {
        node.log(p);
    }
}
else if(p.substring(0,3) === 'RM0') {
    if(log_s_meter) {
        node.log(p);
    }
}
else {
    if(log_other) {
        node.log(p);
    }
}

