'use strict';

const POLL_DELAY = 25;

function sendNextEx() {
    const RigSetting = global.get('RigSetting');

    let next_menu_item = flow.get('next_menu_item');

    if(next_menu_item === 154) {
        next_menu_item = 1;
    }

    flow.set('next_menu_item', next_menu_item + 1);

    setTimeout(function() {
        node.send([{ payload: RigSetting.of('MENU', {no: next_menu_item}).asRead }, null]);
        flow.set('last_ex', Date.now());
    }, POLL_DELAY);

    return next_menu_item;
}

if(msg.payload.substring(0,2) === 'EX') {
    if(sendNextEx() === 1 && flow.get('initial_load')) {
        flow.set('initial_load', false);

        node.send([null, {payload: 'end_of_load'}]);
    }
}
else {
    if(Date.now() - flow.get('last_ex') > 5000) {
        node.warn('Polling for MENU changes has timed out, restarting');
        sendNextEx();
    }
}

node.done();

const next_setting = flow.get('next_setting');

if(next_setting.length) {
    const ns = next_setting.shift();

    flow.set('next_setting', next_setting);

    return { payload: ns };
}

return null;

'use strict';

if(msg.uibuilderCtrl === 'ready for content') {
    return {payload: 'init_ui'};
}

return null;

'use strict';

if(msg.name === 'alc_meter') {
    const alc_status = flow.get('alc_status'),
          status = msg.value > 5;

    if(alc_status === null || alc_status !== status) {
        flow.set('alc_status', status);
        return {name: 'alc_status', value: status};
    }
}

return null;

'use strict';

if(msg.name === 'transmit') {
    if(msg.value) {
        const uiController = flow.get('uiController');

        return uiController.read('alc_meter');
    }
    else {
        return {reset: true};
    }
}

return null;

'use strict';

if(msg.name === 'transmit') {
    if(msg.value) {
        const uiController = flow.get('uiController');

        return uiController.read('swr_meter');
    }
    else {
        return {reset: true};
    }
}

return null;

'use strict';

if(flow.get('needsInit')) {
    const MemoryChannels = flow.get('MemoryChannels'),
          MenuItems = flow.get('MenuItems'),
          OpModes = flow.get('OpModes'),
          UiCache = flow.get('UiCache'),
          UiController = flow.get('UiController');

    const memoryChannels = new MemoryChannels(),
          menuItems = new MenuItems(),
          opModes = new OpModes(),
          uiCache = new UiCache(),
          uiController = new UiController(memoryChannels, menuItems, opModes, uiCache);

    memoryChannels.uiController = uiController;
    memoryChannels.uiCache = uiCache;

    opModes.uiController = uiController;
    opModes.uiCache = uiCache;

    flow.set('memoryChannels', memoryChannels);
    flow.set('menuItems', menuItems);
    flow.set('opModes', opModes);
    flow.set('uiCache', uiCache);
    flow.set('uiController', uiController);

    flow.set('needsInit', false);
}

function initUi() {
    const opModes = flow.get('opModes'),
          uiCache = flow.get('uiCache'),
          memoryChannels = flow.get('memoryChannels');

    opModes.initUi( m => node.send([m.ui, null, m.rig]) );

    uiCache.forEach( msg => node.send([msg, null, null]) );

    memoryChannels.initUi( msg => node.send([msg, null, null]));
}

const uiController = flow.get('uiController'),
      opModes = flow.get('opModes'),
      p = msg.payload;

if(p === 'end_of_load') {
    const tom = opModes.checkTriggers();

    if(tom) {
        opModes.loadOpMode(tom, m => node.send([m.ui, null, m.rig]) );
    }
    else {
        opModes.loadLastOpMode( m => node.send([m.ui, null, m.rig]) );
    }

    initUi();
}
else if(p === 'init_ui') {
    initUi();
}
else if(uiController.canEmit(p.topic)) {
    const uiCache = flow.get('uiCache');

    uiController.emit(p.topic, p, msg => {
        if(msg.ui) {
            const ui = msg.ui;

            uiCache.set(ui);

            if(opModes.isReady()) {
                const tom = opModes.checkTriggers(ui);

                if(tom) {
                    opModes.loadOpMode(tom, m => node.send([m.ui, null, m.rig]) );

                    initUi();
                }
            }

            node.send([ui, null, null]);
        }

        if(msg.local) {
            if(opModes.isReady()) {
                node.send([null, msg.local, null]);
            }
        }

        if(msg.rig) {
            node.send([null, null, msg.rig]);
        }
    });
}

node.done();

