'use strict';

class UiController {
    constructor(memoryChannels, menuItems, opModes, uiCache) {
        this._memoryChannels = memoryChannels;
        this._menuItems = menuItems;
        this._opModes = opModes;
        this._uiCache = uiCache;

        this._hasInit = false;
        this._ = global.get('_');

        const RigSetting = global.get('RigSetting');

        function defaultEmitter(rs, f) {
            f({ui: {name: this.ui, value: rs.value}});
        }

        function defaultHandler(event, value, f) {
            if(event === 'set') {

                f({rig: {payload: RigSetting.of(this.setting, value).asSet}});
            }
            else {
                throw new Error(this.ui + ': unknown event: ' + event);
            }
        }

        function simpleCommandHandler(event, value, f) {
            f({rig: {payload: RigSetting.of(this.setting).asSet}});
        }

        function _toggle(event, om, uc, f) {
            if(event === 'toggle_fav') {
                om.fav = !(om.fav || false);
            }

            if(event === 'toggle_saved') {
                om.saved = !(om.saved || false);
            }

            f({ui: uc});
        }

        function toggleHandler(event, value, f) {
            if(event === 'toggle') {
                const uc = this._uiCache.get(this.ui);

                f({rig: {payload: RigSetting.of(this.setting, !uc.value).asSet}});
            }
            else if(event === 'set') {
                f({rig: {payload: RigSetting.of(this.setting, value).asSet}});
            }
            else {
                throw new Error(this.ui + ': unknown event: ' + event);
            }
        }

        function defaultReadOnlyHandlerr(event, value, f) {
            if(event === 'read') {
                return f({rig: {payload: RigSetting.of(this.setting).asRead}});
            }
            else {
                throw new Error(this.ui + ': unknown event: ' + event);
            }
        }

        this.rig_events = {};
        this.ui_events = {};
        this.has_changed = {};
        this.save_as = {};
        this.is_info = {};

        this._ui_config = [
            {
                setting: 'AF_GAIN',
                ui: 'af_gain',
                emitter: defaultEmitter,
                handler: defaultHandler
            },
            {
                setting: 'AGC_FUNCTION',
                ui: 'agc',
                emitter: defaultEmitter,
                handler(event, value, f) {
                    if(event === 'set') {
                        f({rig: {payload: RigSetting.of(this.setting, this.saveAs(value)).asSet}});
                    }
                    else {
                        throw new Error(this.ui + ': unknown event: ' + event);
                    }
                },
                hasChanged(orig, current) {
                    return orig !== this.saveAs(current);
                },
                saveAs(value) {
                    switch(value) {
                    case 'OFF':
                    case 'FAST':
                    case 'MID':
                    case 'SLOW':
                        return value;

                    case 'AUTO-FAST':
                    case 'AUTO-MID':
                    case 'AUTO-SLOW':
                    case 'AUTO':
                        return 'AUTO';
                    }

                    node.warn(this.ui + ': Unknown value:' + value);
                },
            },
            {
                setting: 'ANTENNA_TUNER_CONTROL',
                ui: 'tuner',
                emitter: defaultEmitter,
                handler(event, value, f) {
                    const ac = this._uiCache.get(this.ui);

                    if(event === 'click') {
                        f({rig: {payload: RigSetting.of(this.setting, ac.value === 'on' ? 'off' : 'on').asSet}});
                    }
                    else if(event === 'dblclick') {
                        f({rig: {payload: RigSetting.of(this.setting, 'tune').asSet}});
                        flow.set('atu_tuning', true);
                    }
                    else if(event === 'set') {
                        f({rig: {payload: RigSetting.of(this.setting, value).asSet}});
