                    else {
                        throw new Error(this.ui + ': unknown event: ' + event);
                    }
                },
            },
            {
                setting: 'APF_FREQ',
                ui: 'apf_freq',
                emitter: defaultEmitter,
                handler: defaultHandler,
            },
            {
                setting: 'APF_ON/OFF',
                ui: 'apf_on',
                emitter: defaultEmitter,
                handler: defaultHandler,
            },
            {
                setting: 'AUTO_NOTCH',
                ui: 'auto_notch',
