            },
            {
                setting: 'BAND_DOWN',
                ui: 'band_down',
                handler: simpleCommandHandler,
            },
            {
                setting: 'BAND_SELECT',
            },
            {
                setting: 'BAND_UP',
                ui: 'band_up',
                handler: simpleCommandHandler,
