                ui: 'transmit',
                emitter(rs, f) {
                    const msg = {name: this.ui, value: rs.value};

                    f({ui: msg, local: msg});
                },
                handler: defaultHandler,
            },
            {
                setting: 'UP',
                ui: 'mic_up',
                handler: simpleCommandHandler,
            },
            {
                setting: 'V/M_KEY_FUNCTION',
            },
            {
                setting: 'VFO-A_TO_MEMORY_CHANNEL',
            },
            {
                setting: 'VFO-A_TO_VFO-B',
                ui: 'vfo_a_to_b',
                handler: simpleCommandHandler,
            },
            {
                setting: 'VFO-B_TO_VFO-A',
                ui: 'vfo_b_to_a',
                handler: simpleCommandHandler,
            },
            {
                setting: 'VOX_DELAY_TIME_/_DATA_VOX_DELAY_TIME',
            },
            {
                setting: 'VOX_GAIN',
                ui: 'vox_gain',
                emitter: defaultEmitter,
                handler: defaultHandler,
            },
            {
