            },
            {
                setting: 'WIDTH',
            },
            {
                setting: 'ZERO_IN',
                ui: 'zero_in',
                handler: simpleCommandHandler,
            },
        ];

        const baseContext = {
            _memoryChannels: this._memoryChannels,
            _menuItems: this._menuItems,
            _opModes: this._opModes,
            _uiCache: this._uiCache,
        };

        this._ui_config.forEach( e => {
            if(e.hasOwnProperty('emitter')) {
                if(!e.hasOwnProperty('setting')) {
                    throw new Error('ui_config has emitter, but no setting: ' + JSON.stringify(e));
                }

                if(this.rig_events.hasOwnProperty(e.setting)) {
                    throw new Error('Duplicate setting in ui_config: ' + e.setting);
                }

                this.rig_events[e.setting] = Object.assign({}, baseContext, e);
            }

            if(e.hasOwnProperty('handler')) {
                if(!e.hasOwnProperty('ui')) {
                    throw new Error('ui_config has handler, but no ui: ' + JSON.stringify(e));
                }

                if(this.ui_events.hasOwnProperty(e.ui)) {
                    throw new Error('ui_events has dup ui - ' + ui_events[e.ui].setting + ' and ' + key);
                }

                this.ui_events[e.ui] = Object.assign({}, baseContext, e);
            }

            if(e.hasOwnProperty('hasChanged')) {
                if(e.hasOwnProperty('setting')) {
                    this.has_changed[e.setting] = Object.assign({}, baseContext, e);
                }
                if(e.hasOwnProperty('ui')) {
                    this.has_changed[e.ui] = Object.assign({}, baseContext, e);
                }
            }

            if(e.hasOwnProperty('saveAs')) {
                if(e.hasOwnProperty('setting')) {
                    this.save_as[e.setting] = Object.assign({}, baseContext, e);
                }
                if(e.hasOwnProperty('ui')) {
                    this.save_as[e.ui] = Object.assign({}, baseContext, e);
                }
            }

            if(e.hasOwnProperty('isInfo')) {
                if(e.hasOwnProperty('setting')) {
                    this.is_info[e.setting] = Object.assign({}, baseContext, e);
                }
                if(e.hasOwnProperty('ui')) {
                    this.is_info[e.ui] = Object.assign({}, baseContext, e);
                }
            }
        });
    }

    canEmit(name) {
        return this.rig_events.hasOwnProperty(name);
    }
    emit(name, rs, f) {
        const re = this.rig_events[name];

        if(re) {
            re.emitter.call(re, rs, f);
        }
        else {
            throw new Error(name + ': rig event emitted without emitter');
        }
    }

    handle(name, event, value, f) {
        const ue = this.ui_events[name];

        if(ue) {
            ue.handler.call(ue, event, value, f);
        }
        else {
            throw new Error(name + ': ui event emitted without handler');
        }
    }
    read(name) {
        let rig = null;

        this.handle(name, 'read', null, m => {
            if(m.hasOwnProperty('rig')) {
                if(rig) {
                    throw new Error(name + ': read() didn\'t expect multiple rig output.');
                }
                rig = m.rig;
            }
            if(m.hasOwnProperty('ui')) {
                throw new Error(name + ': read() didn\'t expect ui output.');
            }
        });

        if(rig) {
            return rig;
        }
        throw new Error(name + ': read() failed to get rig output.');
    }

    hasChanged(name, orig, current) {
        const hc = this.has_changed[name];

        if(hc) {
            return hc.hasChanged.call(hc, orig, current);
        }
        else {
            return !this._.isEqual(orig, current);
        }
    }
    saveAs(name, value) {
        const sa = this.save_as[name];

        if(sa) {
            return sa.saveAs.call(sa, value);
        }
        else {
            return value;
        }
    }
    isInfo(name) {
        const ii = this.is_info[name];

        if(ii) {
            return ii.isInfo || false;
        }
        return false;
    }
}

