'use strict';

class UiCache {
    constructor() {
        this._ui_cache = {},
        this._ = global.get('_');
    }

    set(value) {
        if(value.name === 'menu_item') {
            this._ui_cache[value.no] = this._.cloneDeep(value);
        }
        else {
            this._ui_cache[value.name] = this._.cloneDeep(value);
        }
    }

    get(name) {
        return this._.cloneDeep(this._ui_cache[name]);
    }

    forEach(f) {
        for (const [k, v] of Object.entries(this._ui_cache)) {
            f(v);
        }
    }
}

flow.set('UiCache', UiCache);

'use strict';

class OpModes {
    constructor() {
        this.uiController = null;
        this.uiCache = null;

        this.NEW_OP_MODE = 'New Op Mode';
        this.NEW_OP_MODE_PROP = 'new_op_mode';
        this._ = global.get('_');

        this._com = null;
        this._manualMode = false;
        this.triggerCache = null;
        this.lastLoad = 0;
        this.opModes = global.get('op_modes', 'persist') || {};
    }

    checkTriggers(trigger_ui) {
        if(this._manualMode || !this.isReady()) {
            return null;
        }

        // NOTE: This code delays the checking of triggers for a
        // period of time after the last load.  What happens is
        // if a mode setting is set, the rig will emit a vfo_a
        // message.  What's happening is the Op Mode gets changed
        // and then the vfo_a message will trigger another Op Mode
        // change.  Now what we want.

        if(Date.now() - this.lastLoad < 2000) {
            return null;
        }

        if(!this.triggerCache) {
            this.triggerCache = [];

            this._.sortBy(this.opModes, [ om => om.order ]).forEach( om => {
                if(om.trigger && om.enabled) {
                    this.triggerCache.push({
                        name: om.name,
                        primary: om.primary,
                        order: om.order,
                        trigger: new Function(om.trigger),
                    });
                }
            });
        }

        for(const tc of this.triggerCache) {
            const context = {
                UiCache: this.uiCache,
                node: node,
                trigger: trigger_ui,
            };

            if(tc.trigger.call(context)) {
                if(tc.name !== this._com.name) {
                    return tc.name;
                }
                else {
                    return null;
                }
            }
        }

        return null;
    }

    currentOpMode() {
        if(this._com) {
            return this._com;
        }

        throw new Error('Current Op Mode not set?');
    }

    getJson() {
        return JSON.stringify(this.opModes);
    }

    _sendOpMode(f) {
        f({
            ui: {
                name: 'op_mode',
                value: {
                    name: this._com.name,
                    primary: this._com.primary,
                    enabled: this._com.enabled,
                    order: this._com.order,
                    enableTimer: this._com.enableTimer,
                    enableCountdown: this._com.enableCountdown,
                    startTime: this._com.startTime,
                    trigger: this._com.trigger
                }
            }
        });
    }
    initUi (f) {
        if(this._com) {
            this._sendOpMode(f);

            const names = Object.keys(this.opModes).sort().map( name => { return {name: name, can_delete: true} });
            names.push({name: this.NEW_OP_MODE, can_delete: false});

            f({ui: {name: 'op_modes', value: names}});
            f({ui: {name: 'manual_mode', value: this._manualMode}});
        }
    }

    isReady() {
        if(this._com) {
            return true;
        }
        return false;
    }

    _lastOpMode(op_mode) {
        if(op_mode) {
            global.set('last_op_mode', op_mode, 'persist');
        }

        return global.get('last_op_mode', 'persist');
    }
    loadLastOpMode(f) {
        const lom = this._lastOpMode();

        if(lom) {
            if(this.opModes.hasOwnProperty(lom)) {
                return this.loadOpMode(lom, f);
            }

            node.warn('loadLastOpMode lom not found: ' + lom);
        }

        this.loadOpMode(this.NEW_OP_MODE, f);
    }

    _sendSetting(name, rc, f) {
        this.uiController.handle(name, 'set', rc.value, f);
    }
    _newOpMode(name, prop) {
        const om = {
            name: name,
            primary: false,
            enabled: true,
            order: 0,
            enableTimer: false,
            enableCountdown: false,
            startTime: 90,
            trigger: '',
            settings: {},
            menus: {}
        };

        om[prop] = true;

        return om;
    }
    _loadOpMode(name) {
        if(name === this.NEW_OP_MODE) {
            return this._newOpMode('New Op Mode', this.NEW_OP_MODE_PROP);
        }

        if(this.opModes.hasOwnProperty(name)) {
            return this.opModes[name];
        }

        throw new Error('loadOpMode: Op Mode "' + name + '" not found.');
    }
    loadOpMode(name, f) {
        const op_mode = this._loadOpMode(name);

        this.lastLoad = Date.now();
        node.log('Loading Op Mode: ' + name);

        this._com = this._.cloneDeep(op_mode);
        this._lastOpMode(this._com.name);

        // Make a copy of current values of rig on load

        let fix_freq = false;
        this.uiCache.forEach( uc => {
            if(uc.name === 'menu_item') {
                if(!this._com.menus.hasOwnProperty(uc.no)) {
                    this._com.menus[uc.no] = {};
                }
                if(this._com.menus[uc.no].saved) {
                    this._com.menus[uc.no].onLoad = this._com.menus[uc.no].value;

                    if(f && !this._.isEqual(this._com.menus[uc.no].value, uc.value)) {
                       this._sendSetting('menu_item', {value: {no: uc.no, value: this._com.menus[uc.no].value}}, f);
                    }
                }
                else {
                    this._com.menus[uc.no].onLoad = uc.value;
                }
            }
            else {
                if(!this._com.settings.hasOwnProperty(uc.name)) {
                    this._com.settings[uc.name] = {};
                }
                if(this._com.settings[uc.name].saved) {
                    this._com.settings[uc.name].onLoad = this._com.settings[uc.name].value;
                    if(f && this.uiController.hasChanged(uc.name, this._com.settings[uc.name].value, uc.value)) {
                        this._sendSetting(uc.name, this._com.settings[uc.name], f);

                        if(uc.name === 'mode') {
                            fix_freq = true;
                        }
                    }
                }
                else {
                    this._com.settings[uc.name].onLoad = uc.value;
                }
            }
        });

        // FIXME:  mode change can change the VFO freq.
        // e.g. switching from DATA-USB to LSB will shift the VFO 700 Hz

        if(f && fix_freq) {
            const uc = this.uiCache.get('vfo_a');

            this._sendSetting(uc.name, uc, f);
        }

        this._sendOpMode(f);
    }

    loadPrimary(f) {
        const primary = Object.values(this.opModes).find( om => om.primary );

        if(primary) {
            return this.loadOpMode(primary.name, f);
        }
    }

    remove(name) {
        delete this.opModes[name];
        this.triggerCache = null;

        global.set('op_modes', this.opModes, 'persist');
    }

    saveJson(json) {
        const Ajv = global.get('Ajv');

        const ajv = new Ajv({allowUnionTypes: true})

        const schema = {
            "type": "object",
            "patternProperties": {
                "^.*$": {
                    "type": "object",
                    "required": ["name"],
                    "properties": {
                        "name": {
                            "type": "string"
                        },
                        "primary": {
                            "type": "boolean"
                        },
                        "enabled": {
                            "type": "boolean"
                        },
                        "order": {
                            "type": "number"
                        },
                        "enableTimer": {
                            "type": "boolean"
                        },
                        "enableCountdown": {
                            "type": "boolean"
                        },
                        "startTime": {
                            "type": "number"
                        },
                        "trigger": {
                            "type": "string"
                        },
                        "settings": {
                            "type": "object",
                            "patternProperties": {
                                "^.*$": {
                                    "type": "object",
                                    "required": [],
                                    "properties": {
                                        "saved": {
                                            "type": "boolean"
                                        },
                                        "value": {
                                            "type": ["boolean", "number", "null", "string"]
                                        },
                                        "fav": {
                                            "type": "boolean"
                                        }
                                    }
                                }
                            }
                        },
                        "menus": {
                            "type": "object",
                            "patternProperties": {
                                "^[0-9]+$": {
                                    "type": "object",
                                    "required": [],
                                    "properties": {
                                        "saved": {
                                            "type": "boolean"
                                        },
                                        "value": {
                                            "type": ["boolean", "number", "null", "string"]
                                        },
                                        "fav": {
                                            "type": "boolean"
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }
        };

        const validate = ajv.compile(schema)

        if(!validate(json)) {
node.log('ajv.validate: ' + JSON.stringify(validate.errors));
            return validate.errors[0].dataPath + ' ' +validate.errors[0].message;
        }

        this.opModes = json;
        this.triggerCache = null;
        global.set('op_modes', this.opModes, 'persist');

        return null;
    }

    saveOpMode() {
        const som = {};

        if(this._com[this.NEW_OP_MODE_PROP]) {
            return 'Please change Op Mode name before saving.';
        }

        som.name = this._com.name;
        som.primary = this._com.primary;
        som.enabled = this._com.enabled;
        som.order = this._com.order;
        som.enableTimer = this._com.enableTimer;
        som.enableCountdown = this._com.enableCountdown;
        som.startTime = this._com.startTime;
        som.trigger =this._com.trigger;

        som.settings = {};
        for (const [k, v] of Object.entries(this._com.settings)) {
            if(v.saved) {
                if(!som.settings[k]) {
                    som.settings[k] = {};
                }
                som.settings[k].saved = true;
                som.settings[k].value = this.uiController.saveAs(k, this.uiCache.get(k).value);
            }
            if(v.fav) {
                if(!som.settings[k]) {
                    som.settings[k] = {};
                }
                som.settings[k].fav = true;
            }
        }

        som.menus = {};
        for (const [k, v] of Object.entries(this._com.menus)) {
            if(v.saved) {
                if(!som.menus[k]) {
                    som.menus[k] = {};
                }
                som.menus[k].saved = true;
                som.menus[k].value = this.uiCache.get(k).value;
            }
            if(v.fav) {
                if(!som.menus[k]) {
                    som.menus[k] = {};
                }
                som.menus[k].fav = true;
            }
        }

        if(som.primary) {
            Object.values(this.opModes).forEach( om => om.primary = false );
        }

        this.opModes[som.name] = som;
        global.set('op_modes', this.opModes, 'persist');
        this.triggerCache = null;

        return null;
    }

    toggleManualMode(value) {
        this._manualMode = !this._manualMode;
    }

    updateOpMode(value) {
        const props = ['name', 'primary', 'enabled', 'order', 'enableTimer', 'enableCountdown', 'startTime', 'trigger'];

        for(const prop of props) {
            if(value.hasOwnProperty(prop)) {
                if(this._com[prop] !== value[prop]) {
                    this._com[prop] = value[prop];
                }
            }
        }

        delete this._com[this.NEW_OP_MODE_PROP];
        return this._com;
    }

}
flow.set('OpModes', OpModes);

