'use strict';

class MemoryChannels {
    constructor() {
        this.uiController = null;
        this.uiCache = null;

        this._memory_channels = global.get('memory_channels', 'persist') || [];
        this._ = global.get('_');
    }

    _setSetting(name, value, f) {
        const uc = this.uiCache.get(name);

        if(this.uiController.hasChanged(name, uc.value, value)) {
            this.uiController.handle(name, 'set', value, f);
        }
    }

    newChannel() {
        return {
            name: 'New Memory Channel',
            frequency: 7000000,
            offset: 'Simplex',
            mode: 'USB',
            squelchMode: 'CTCSS OFF',
            squelchTone: null,
            isNew: true,
        };
    }

    init() {
        this._memory_channels = [];

        this.save();
    }

    update(mc) {
        if(mc.hasOwnProperty('no')) {
            this._memory_channels[mc.no] = Object.assign({}, mc);
        }
        else {
            this._memory_channels.push(Object.assign({}, mc, {no: this._memory_channels.length}));
        }

        this.save();
    }

    remove(no) {
        const mc = this._memory_channels[no];

        if(mc) {
            this._memory_channels[no] = null;

            this.save();
        }
    }

    get(no) {
        const mc = this._memory_channels[no];

        if(mc) {
            return Object.assign({}, mc);
        }
        return null;
    }

    load(no, f) {
        const mc = this._memory_channels[no];

        if(mc) {
            node.log('Loading Memory Channel: ' + mc.name);
            this._setSetting('mode', mc.mode, f);
            this._setSetting('vfo_a', mc.frequency, f);
            this._setSetting('offset', mc.offset, f);
            this._setSetting('ctcss', mc.squelchMode, f);

            switch(mc.squelchMode) {
            case 'CTCSS OFF':
                break;
            case 'CTCSS ENC/DEC':
            case 'CTCSS ENC':
                this._setSetting('ctcss_tone_frequency', mc.squelchTone, f);
                break;
            case 'DCS ENC/DEC':
            case'DCS ENC':
                this._setSetting('dcs_tone_frequency', mc.squelchTone, f);
                break;
            }
        }
    }

    save() {
        global.set('memory_channels', this._memory_channels,  'persist');
    }

    initUi(f) {
        f({name: 'memory_channels',
            value: this._.sortBy(this._memory_channels.filter( mc => mc != null), [ mc => mc.name ])
        });
    }

    getJson(f) {
        const mcs = this._memory_channels.filter( mc => mc != null );

        f({ui: {name: 'export_json', value: JSON.stringify(mcs)}});
    }

    saveJson(json) {
        const Ajv = global.get('Ajv');

        const ajv = new Ajv()

        const schema = {
            "type": "array",
            "items": {
                "type": "object",
                "required": ["no", "name", "frequency", "mode"],
                "properties": {
                    "no": {
                        "type": "number"
                    },
                    "name": {
                        "type": "string"
                    },
                    "frequency": {
                        "type": "number"
                    },
                    "opMode": {
                        "type": "string"
                    },
                    "mode": {
                        "type": "string",
                        "enum": ['LSB', 'USB', 'FM', 'DATA-USB', 'DATA-FM', 'RTTY-LSB', 'RTTY-USB', 'CW', 'CW-R', 'DATA-LSB', 'AM', 'AM-N', 'FM-N', 'C4FM']
                    },
                    "offset": {
                        "type": "string",
                        "enum": ['Simplex','+','-']
                    },
                    "squelchMode": {
                        "type": "string",
                        "enum": ['CTCSS OFF', 'CTCSS ENC/DEC', 'CTCSS ENC', 'DCS ENC/DEC', 'DCS ENC']
                    },
                    "squelchTone": {
                        "type": ["string", "null"]
                    }
                }
            }
        };

        const validate = ajv.compile(schema)

        if (!validate(json)) {
            console.log(validate.errors);
            return validate.errors[0].dataPath + ' ' +validate.errors[0].message;
        }

        this._memory_channels = json;
        this._memory_channels.forEach( (mc, no) => {
            mc.no = no;
        });
        global.set('memory_channels', this._memory_channels,  'persist');

        return null;
    }
}

