const op_modes = global.get('op_modes', 'persist');

msg.payload = op_modes;
msg.filename = 'op_modes.json';

return msg;

const memory_channels = global.get('memory_channels', 'persist');

msg.payload = memory_channels;
msg.filename = 'memory_channels.json';

return msg;

'use strict';

if(msg.name === 'transmit') {
    let interval = flow.get('interval');

    if(msg.value && !interval) {
        let count = 0;

        interval = setInterval(function() {
            count++;
            node.send({name: 'interval', value: count});
        }, 1000);
        flow.set('interval', interval);
    }

    if(!msg.value && interval) {
        clearInterval(interval);
        flow.set('interval', null);
    }
}

'use strict';

if(msg.event === 'clear') {
    const activators = flow.get('activators', 'persist');

    Object.values(activators).forEach(a => {
        a.status = 'new';
        a.selected = false;
    });

    return [{payload: 'refresh'}, null, null];
}

if(msg.event === 'refresh') {
    return [{payload: 'refresh'}, null, null];
}

if(msg.event === 'selected') {
    const activators = flow.get('activators', 'persist');
    const a = activators[msg.value];

    if(a) {
        const wasSelected = a.selected;

        Object.values(activators).forEach(b => {
            if(b.selected) {
                b.selected = false;
                node.send([null, {name: 'selected', id: b.id, value: b.selected}, null])
            }
        });

        if(!wasSelected) {
            a.selected = true;
            node.send([null, {name: 'selected', id: a.id, value: a.selected}, null])

            if(a.status === 'new') {
                a.status = 'old';
                node.send([null, {name: 'status', id: a.id, value: a.status}, null])
            }

            flow.set('activators', activators, 'persist');

            const frequency = (parseFloat(a.frequency) * 1000).toFixed();

            const uiController = flow.get('uiController');

            uiController.handle('vfo_a', 'set', frequency, m => node.send([null, null, m.rig]));
        }
        else {
            node.send([null, {name: 'status', id: a.id, value: a.status}, null])
        }

        node.done();
    }
}

if(msg.event === 'skip' || msg.event === 'worked') {
    const activators = flow.get('activators', 'persist');
    const a = activators[msg.value];

    if(a) {
        a.status = a.status === msg.event ? 'old' : msg.event;
        flow.set('activators', activators, 'persist');

        return [null, {name: 'status', id: a.id, value: a.status}, null];
    }
}

return [null, null, null];

if(msg.uibuilderCtrl === 'ready for content') {
    return {payload: ''}
}

return null;

'use strict'

let activators = msg.payload.sort((a, b) => {
    fa = parseFloat(a.frequency);
    fb = parseFloat(b.frequency);

    if(fa < fb) {
        return -1;
    }
    if(fa > fb) {
        return 1;
    }
    return 0;
})

let oa = flow.get('activators', 'persist');
if(!oa) {
    oa = {};
}
let na = {};

activators.forEach(a => {
    const id = a.activator + '/' + a.reference + '/' + a.mode;

    a.id = id;
    if(oa.hasOwnProperty(a.id)) {
        a.status = oa[a.id].status;
        a.selected = oa[a.id].selected;
    }
    else {
        a.status = 'new';
        a.selected = false;
    }

    na[a.id] = a;
});

flow.set('activators', na, 'persist');

return {name: 'activators', value: activators};

