'use strict;'

const RigSetting = global.get('RigSetting'),
      RigState = global.get('RigState'),
      needs_update = flow.get('needs_update'),
      debounce = flow.get('debounce'),
      p = msg.payload;

if(p === '?;') {
    node.send([{payload: '?; found in input'}, null, null]);
}
// else if(p.substring(0,2) !== 'VX') {

// }
else if(p) {
    const rs = RigSetting.fromAnswer(p);

    // Send (or debounce) changes in rig state to GUI

    if(rs.debounce) {

        // Debounce the frequency changes

        debounce[rs.name] = rs;
        flow.set('debounce', debounce);

        RigState.setRigSetting(rs);
    }
    else if(rs.name === 'RM0') {
        const tx = RigState.getRigSetting('TX_SET');

        let xrs = null;

        if(tx.value) {
            xrs = RigSetting.of('READ_METER_-_PO', rs.value);
        }
        else {
            xrs = RigSetting.of('READ_METER_-_S_METER', rs.value);
        }

        node.send([null, {topic: xrs.topic, payload: xrs}, null]);
    }
    else if(rs.name === 'RM2') {
        const ms = RigState.getRigSetting('METER_SW');

        switch(ms.value) {
        case 'COMP':    xrs = RigSetting.of('READ_METER_-_COMP', rs.value); break;
        case 'ALC':     xrs = RigSetting.of('READ_METER_-_ALC', rs.value); break;
        case 'PO':      xrs = RigSetting.of('READ_METER_-_PO', rs.value); break;
        case 'SWR':     xrs = RigSetting.of('READ_METER_-_SWR', rs.value); break;
        case 'ID':      xrs = RigSetting.of('READ_METER_-_ID', rs.value); break;
        case 'VDD':     xrs = RigSetting.of('READ_METER_-_VDD', rs.value); break;
        default:
            node.warn('Failed to find meter translation, RM: ' + rs.value + ', MS: ' + ms.value);
            break;
        }

        node.send([null, {topic: xrs.topic, payload: xrs}, null]);
    }
    else {

        // Send changes to rig state in real time.

        if(RigState.hasChanged(rs)) {
            node.send([null, {topic: rs.topic, payload: rs}, null]);
        }
    }

    // Send any replies to queries back to tye aux tty

    if(needs_update.length) {
        const still_needs_updating = [];

        needs_update.forEach(name => {
            if(name === rs.name) {
                node.send([null, null, {payload: rs.rawAnswer}]);
            }
            else {
                still_needs_updating.push(name);
            }
        });
        flow.set('needs_update', still_needs_updating);
    }

    // Cache non transient query commands

    if(rs.isCacheable) {
        RigState.setRigSetting(rs);
    }
}
else {
    node.send(['Received empty payload?', null, null]);
}

node.done();

return [null, null, null];

'use strict;'

const RigSetting = global.get('RigSetting'),
      RigState = global.get('RigState'),
      needs_update = flow.get('needs_update'),
      p = msg.payload.toUpperCase();

if(p) {
    const rs = RigSetting.fromAnswer(p);

    if(rs.desc === 'AUTO_INFORMATION') {
        if(rs.value) {
            node.warn('### Got AI1;');
        }
        else {
            node.warn('### Got AI0;');
        }
        return [{payload: '?;'}, null];
    }

    // If a set command, forward it out to the rig

    if(rs.sendToRig) {
        return [null, {payload: rs.rawAnswer}];
    }

    // See if answer is already in cache

    const crs = RigState.findRigSetting(rs.name);

    if(crs) {

        // In cache, return answer

        return [{payload: crs.rawAnswer}, null];
    }

    // Not in cache, let rig input handler know the answer needs
    // to passed along.

    needs_update.push(rs.name);
    flow.set('needs_update', needs_update);

    // Non-cacheable commands always go out, as well
    // as cache misses

    return [null, {payload: p}];
}

node.warn('Received empty payload?');

return [null, null];

