'use strict';

const RigSetting = global.get('RigSetting');

// UI Iterface functions

function uiMessage(no, value) {
	return {config: {no: no, value: value}};
}

function defaultToUi (no, value) {
	return uiMessage(no, value);
}

function toUiDataMode(no, value) {
	return uiMessage(no, value === '0' ? 'PSK' : 'OTHER');
}

function toUiDataPortSelect(no, value) {
	return uiMessage(no, value === '1' ? 'DATA' : 'USB');
}

function toUiFreq(value, offset) {
    if(value === 0) {
        return 'OFF';
    }

    return ((value + offset) * 50).toString();
}

function toUiMicSelect(no, value) {
	return uiMessage(no, value === '0' ? 'MIC' : 'REAR');
}

function toUiPttSelect(no, value) {
    switch(value) {
    case '0': return uiMessage(no, 'DAKY');
    case '1': return uiMessage(no, 'RTS');
    case '2': return uiMessage(no, 'DTR');
    }
}

function toUiSimpleValue(no, value) {
	return uiMessage(no, parseInt(value, 10).toString());
}

function toUiPcKeying(no, value) {
    switch(value) {
    case '0': return uiMessage(no, 'OFF');
    case '1': return uiMessage(no, 'DAKY');
    case '2': return uiMessage(no, 'RTS');
    case '3': return uiMessage(no, 'DTR');
    }
}

function toUiVoicePortSelect(no, value) {
	return uiMessage(no, value === '0' ? 'DATA' : 'USB');
}

function cutFreq(value, offset) {
    if(value === 0) {
        return 'OFF';
    }

    return ((value + offset) * 50).toString();
}
function toUiLCutFreq(no, value) {
	return uiMessage(no, cutFreq(value, 1));
}
function toUiSlope(no, value) {
	return uiMessage(no, value === '0' ? '6 dB' : '18 dB');
}
function toUiHCutFreq(no, value) {
	return uiMessage(no, cutFreq(value, 13));
}

function defaultFromUi (arg) {
    return null;
}

function toCat(no, value) {
    let prefix;

    if(no < 10) {
        prefix = 'EX00' + no;
    }
    else if(no < 100) {
        prefix = 'EX0' + no;
    }
    else {
        prefix = 'EX' + no;
    }

    if(value) {
        return prefix + value + ';';
    }
    return prefix + ';';
}

