    return {payload: RigSetting.of('READ_METER_-_SWR').asRead};
}
else {
    return {reset: true};
}

const next_setting = flow.get('next_setting');

if(next_setting.length) {
    node.send({ payload: next_setting.shift() });
    flow.set('next_setting', next_setting);
}

return null;
