function initUi() {
    const OpModes = flow.get('OpModes'),
          UiCache = flow.get('UiCache');

    OpModes.initUi( msg => node.send([msg, null, null]) );

    UiCache.forEach( msg => node.send([msg, null, null]));
}

const OpModes = flow.get('OpModes'),
      settings_to_ui = flow.get('settings_to_ui'),
      p = msg.payload;

if(p === 'end_of_load') {
    const tom = OpModes.checkTriggers();

    if(tom) {
        OpModes.loadOpMode(tom, (rc) => {
            node.send([null, null, rc]);
        });
    }
    else {
        OpModes.loadLastOpMode( (rc) => {
            node.send([null, null, rc]);
        });
    }
    initUi();
}
else if(p === 'init_ui') {
    initUi();
}
else if(settings_to_ui.hasOwnProperty(p.topic)) {
    const re = settings_to_ui[p.topic],
          ui = re.emit(re.ui, p),
          UiCache = flow.get('UiCache')

    if(ui.name === 'menu_item') {
        UiCache.set(ui.no, ui);
    }
    else {
        UiCache.set(ui.name, ui);
    }

    if(OpModes.isReady()) {
        const tom = OpModes.checkTriggers(ui);

        if(tom) {
            OpModes.loadOpMode(tom, (rc) => {
                node.send([null, null, rc]);
            });
            initUi();
        }

        if(re.hasOwnProperty('local') && re.local) {
            node.send([ui, ui, null]);
        }
    }

    node.send([ui, null, null]);
}

node.done();

