'use strict';

function toggle(msg) {
    const OpModes = flow.get('OpModes'),
          com = OpModes.currentOpMode(),
          UiCache = flow.get('UiCache');
    let om,
        uc;

    if(msg.topic === 'menu_item') {
        if(!com.menus.hasOwnProperty(msg.no)) {
            com.menus[msg.no] = {};
        }
        om = com.menus[msg.no];
        uc = UiCache.get(msg.no);
    }
    else {
        if(!com.settings.hasOwnProperty(msg.topic)) {
            com.settings[msg.topic] = {};
        }
        om = com.settings[msg.topic];
        uc = UiCache.get(msg.topic);
    }

    if(msg.event === 'toggle_fav') {
        om.fav = !(om.fav || false);
    }

    if(msg.event === 'toggle_saved') {
        om.saved = !(om.saved || false);
    }

    // Save changes to this Op Mode to storage

    OpModes.currentOpMode(com)

    return uc;
}

function initUi() {
    const OpModes = flow.get('OpModes'),
          UiCache = flow.get('UiCache');

    OpModes.initUi( msg => node.send([null, msg]) );

    UiCache.forEach( msg => node.send([null, msg]));
}

function handleUiEvent(name, event, value) {
    const ui_to_settings = flow.get('ui_to_settings'),
          ue = ui_to_settings[name];

    if(ue) {
        if(ue.hasOwnProperty('handler')) {
            node.send([{payload: ue.handler.call(ue, event, value)}, null]);
        }
        else {
            node.warn('Received UI event without handler: ' + JSON.stringify(ue));
        }
    }
    else {
        node.warn('Received unknown UI event: ' + name);
    }
}

if(msg.event === 'toggle_fav' || msg.event === 'toggle_saved') {
    node.send([null, toggle(msg)]);
}
else if(msg.topic === 'op_mode') {
    const OpModes = flow.get('OpModes'),
          UiCache = flow.get('UiCache');

    node.log('op_mode event: ' + JSON.stringify(msg));

    switch(msg.event) {
    case 'update':
        const op_mode = OpModes.updateOpMode(msg.value);

        node.send([null, {name: 'op_mode', value: {name: op_mode.name, trigger: op_mode.trigger}}]);
        break;

    case 'save':
        const result = OpModes.saveOpMode();
        if(result) {
            node.send([null, {name: 'alert', value: result}]);
        }
        else {
            OpModes.initUi( msg => node.send([null, msg]) );
        }
        break;

    case 'load':
        OpModes.loadOpMode(msg.value, (rc) => {
node.log('rc: ' + JSON.stringify(rc));
            node.send([rc, null]);
        });
        OpModes.initUi( msg => node.send([null, msg]) );
        UiCache.forEach( msg => node.send([null, msg]));

        break;

    case 'remove':
        OpModes.remove(msg.value);
        OpModes.initUi( msg => node.send([null, msg]) );
        break;

    case 'toggle_manual_mode':
        OpModes.toggleManualMode();
        OpModes.initUi( msg => node.send([null, msg]) );
        break
    }
}
else {
    handleUiEvent(msg.topic, msg.event, msg.value);
}

node.done();

'use strict';

const OpModes = flow.get('OpModes'),
      _ = global.get('_');

if(!OpModes.isReady()) {
    return null;
}

if(msg.name === 'op_mode' || msg.name === 'op_modes') {
    return msg;
}

const com = OpModes.currentOpMode();

msg.saved = false;
msg.fav = false;

if(msg.name === 'menu_item') {
    if(com.menus.hasOwnProperty(msg.no)) {
        msg.saved = com.menus[msg.no].saved || false;
        msg.fav = com.menus[msg.no].fav || false;
        msg.changed = !_.isEqual(msg.value, com.menus[msg.no].onLoad);
    }
}
else if(com.settings.hasOwnProperty(msg.name)) {
    msg.saved = com.settings[msg.name].saved || false;
    msg.fav = com.settings[msg.name].fav || false;
    msg.changed = !_.isEqual(msg.value, com.settings[msg.name].onLoad);
}

return msg;

initialize:
'use strict';

const ui_cache = {},
      _ = global.get('_');

const uiCache = {
    set(name, value) {
        if(typeof(value) === 'object') {
            ui_cache[name] = _.cloneDeep(value);
        }
        else {
            ui_cache[name] = value;
        }
    },
    get(name) {
        if(typeof(ui_cache[name]) === 'object') {
            return _.cloneDeep(ui_cache[name]);
        }
        else {
            return ui_cache[name];
        }
    },
    forEach(f) {
        for (const [k, v] of Object.entries(ui_cache)) {
            f(v);
        }
    },
};

flow.set('UiCache', uiCache);

'use strict';

const PERSIST = 'persist',
      OP_MODES = 'op_modes',
      NEW_OP_MODE = 'New Op Mode',
      NEW_OP_MODE_PROP = 'new_op_mode',
      CHANGED_PROP = 'changes',
      op_modes = global.get(OP_MODES, PERSIST) || {},
      trigger_cache = {},
      _ = global.get('_');

let com = null,
    manual_mode = false,
    last_load = 0;

const isReady = function() {
    if(com) {
        return true;
    }
    return false;
}

const currentOpMode = function() {
    if(com) {
        return com;
    }

    throw new Error('Current Op Mode not set?');
}

const lastOpMode = function(op_mode) {
    if(op_mode) {
        global.set('last_op_mode', op_mode, PERSIST);
    }

    return global.get('last_op_mode', PERSIST);
}

const loadLastOpMode = function(f) {
    const lom = lastOpMode();

    if(lom) {
        if(op_modes.hasOwnProperty(lom)) {
            loadOpMode(lom, f);
        }
    }
    else {
        loadOpMode(NEW_OP_MODE);
    }
};

const _newOpMode = function(name, prop) {
    const om = {name: name, trigger: '', settings: {}, menus: {}};

    om[prop] = true;

    return om;
}
function _loadOpMode(name) {
    if(name === NEW_OP_MODE) {
        return _newOpMode('New Op Mode', NEW_OP_MODE_PROP);
    }

    if(op_modes.hasOwnProperty(name)) {
        return op_modes[name];
    }

    throw new Error('loadOpMode: Op Mode "' + name + '" not found.');
}
const _sendSetting = function(name, rc) {
    const ui_to_settings = flow.get('ui_to_settings'),
          ue = ui_to_settings[name];

    if(ue) {
        if(ue.hasOwnProperty('handler')) {
            return {payload: ue.handler.call(ue, 'set', rc.value)};
        }
        else {
            node.warn('Received UI event without handler: ' + JSON.stringify(ue));
        }
    }
    else {
        node.warn('Received unknown UI event: ' + name);
    }

    return null;
}
const loadOpMode = function(name, f) {
    const op_mode = _loadOpMode(name),
          UiCache = flow.get('UiCache');

    last_load = Date.now();
    node.log('Loading Op Mode: ' + name);

    com = _.cloneDeep(op_mode);
    lastOpMode(com.name);

    // Configure rig with new settings

    // if(f) {
    //     for (const [k, v] of Object.entries(com.settings)) {
    //         if(v.saved && v.hasOwnProperty('value')) {
    //             f(_sendSetting(k, v));
    //         }
    //     }

    //     for (const [k, v] of Object.entries(com.menus)) {
    //         if(v.saved && v.hasOwnProperty('value')) {
    //             f(_sendSetting('menu_item', _.cloneDeep({value: {no: k, value: v.value}})));
    //         }
    //     }
    // }

    // Make a copy of current values of rig on load

    let fix_freq = false;
    UiCache.forEach( uc => {
        if(uc.name === 'menu_item') {
            if(!com.menus.hasOwnProperty(uc.no)) {
                com.menus[uc.no] = {};
            }
            if(com.menus[uc.no].saved) {
                com.menus[uc.no].onLoad = com.menus[uc.no].value;

                if(f && !_.isEqual(com.menus[uc.no].value, uc.value)) {
                   f(_sendSetting('menu_item', {value: {no: uc.no, value: com.menus[uc.no].value}}));
                }
            }
            else {
                com.menus[uc.no].onLoad = uc.value;
            }
        }
        else {
            if(!com.settings.hasOwnProperty(uc.name)) {
                com.settings[uc.name] = {};
            }
            if(com.settings[uc.name].saved) {
                com.settings[uc.name].onLoad = com.settings[uc.name].value;
                if(f && !_.isEqual(com.settings[uc.name].value, uc.value)) {

                    f(_sendSetting(uc.name, com.settings[uc.name]));

                    if(uc.name === 'mode') {
                        fix_freq = true;
                    }
                }
            }
            else {
                com.settings[uc.name].onLoad = uc.value;
            }
        }
    });

    // FIXME:  mode change can change the VFO freq.
    // e.g. switching from DATA-USB to LSB will shift the VFO 700 Hz

    if(f && fix_freq) {
        const uc = UiCache.get('vfo_a');

        f(_sendSetting(uc.name, uc));
    }
}

const updateOpMode = function(value) {
    if(value.hasOwnProperty('name')) {
        if(com.name !== value.name) {
            com.name = value.name;

            com[CHANGED_PROP] = true;
        }
    }

    if(value.hasOwnProperty('trigger')) {
        if(com.trigger !== value.trigger) {
            com.trigger = value.trigger;

            com[CHANGED_PROP] = true;

            delete trigger_cache[com.name];
        }
    }

    delete com[NEW_OP_MODE_PROP];
    return com;
}

const saveOpMode = function() {
    const som = {},
          UiCache = flow.get('UiCache');

    if(com[NEW_OP_MODE_PROP]) {
        return 'Please change Op Mode name before saving.';
    }

    som.name = com.name;
    som.trigger =com.trigger;

    som.settings = {};
    for (const [k, v] of Object.entries(com.settings)) {
        if(v.saved) {
            if(!som.settings[k]) {
                som.settings[k] = {};
            }
            som.settings[k].saved = true;
            som.settings[k].value = UiCache.get(k).value;
        }
        if(v.fav) {
            if(!som.settings[k]) {
                som.settings[k] = {};
            }
            som.settings[k].fav = true;
        }
    }

    som.menus = {};
    for (const [k, v] of Object.entries(com.menus)) {
        if(v.saved) {
            if(!som.menus[k]) {
                som.menus[k] = {};
            }
            som.menus[k].saved = true;
            som.menus[k].value = UiCache.get(k).value;
        }
        if(v.fav) {
            if(!som.menus[k]) {
                som.menus[k] = {};
            }
            som.menus[k].fav = true;
        }
    }

    op_modes[som.name] = som;
    global.set(OP_MODES, op_modes, PERSIST);

    return null;
};

const initUi = function(f) {
    const names = [];

    f({name: 'op_mode', value: {name: com.name, trigger: com.trigger}});

    for (const [k, v] of Object.entries(op_modes)) {
        names.push({name: k, can_delete: true});
    }
    names.push({name: NEW_OP_MODE, can_delete: false});

    f({name: 'op_modes', value: names});
    f({name: 'manual_mode', value: manual_mode});
}

const forEach = function(f) {
    for (const [k, v] of Object.entries(com.settings)) {
        f(k, _.cloneDeep(v));
    }
    for (const [k, v] of Object.entries(com.menus)) {
        f('menu_item', _.cloneDeep(v, {value: {no: k, value: v.value}}));
    }
}

const remove = function(name) {
    delete op_modes[name];
    delete trigger_cache[name];

    global.set(OP_MODES, op_modes, PERSIST);
}

const checkTriggers = function(trigger_ui) {
    if(manual_mode || !isReady()) {
        return null;
    }

    // NOTE: This code delays the checking of triggers for a
    // period of time after the last load.  What happens is
    // if a mode setting is set, the rig will emit a vfo_a
    // message.  What's happening is the Op Mode gets changed
    // and then the vfo_a message will trigger another Op Mode
    // change.  Now what we want.

    if(Date.now() - last_load < 2000) {
        return null;
    }

    const UiCache = flow.get('UiCache');

    for (const [name, om] of Object.entries(op_modes)) {
        if(om.trigger && om.name !== com.name) {

            if(!trigger_cache.hasOwnProperty(om.name)) {
                trigger_cache[om.name] = new Function(om.trigger);
            }

            const context = {
                UiCache: UiCache,
                op_mode: om,
                node: node,
                trigger: trigger_ui,
            };

            const result = trigger_cache[om.name].call(context);

            if(result) {
                return om.name;
            }
        }
    }

    return null;
}

const toggleManualMode = function(value) {
    manual_mode = !manual_mode;
}

const OpModes = {
    checkTriggers: checkTriggers,
    currentOpMode: currentOpMode,
    forEach: forEach,
    initUi: initUi,
    isReady: isReady,
    loadLastOpMode: loadLastOpMode,
    loadOpMode: loadOpMode,
    remove: remove,
    saveOpMode: saveOpMode,
    toggleManualMode: toggleManualMode,
    updateOpMode: updateOpMode,
}
flow.set('OpModes', OpModes);
